import React, { useState } from 'react';
import { Form, Input, Radio, Row, Col } from 'antd';

export default function IdentityCard() {

  const [gender, setGender] = useState('male');
  const [maritalStatus, setMaritalStatus] = useState('single');

  const onGenderChange = (e) => {
    setGender(e.target.value);
  };

  const onMaritalStatusChange = (e) => {
    setMaritalStatus(e.target.value);
  };

  return (
    <div className='id'>
      <Row>
        <h1>Tờ khai căn cước công dân trực tuyến</h1>
      </Row>
      <Row gutter={[16, 0]}>
        <Form
          name="basic"
          labelCol={{ span: 8 }}
          wrapperCol={{ span: 16 }}
          style={{ maxWidth: 600 }}
          initialValues={{ remember: true }}
          autoComplete="off"
        >
          <Row gutter={[16, 0]}>
            <Col span={12}>
              <Form.Item
                label="Họ và tên: "
                name="fullname"
                rules={[{ required: true, message: 'Please input your fullname!' }]}
              >
                <Input placeholder='Nhập họ và tên' />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Giới tính: "
                name="gender"
                rules={[{ required: true, message: 'Please select your gender!' }]}
              >
                <Radio.Group onChange={onGenderChange} value={gender}>
                  <Radio style={{ marginRight: 20 }} value={'male'}>Nam</Radio>
                  <Radio value={'female'}>Nữ</Radio>
                </Radio.Group>
              </Form.Item>
            </Col>
          </Row>
          <Row gutter={[16, 0]}>
            <Col span={12}>
              <Form.Item
                label="Tình trạng hôn nhân: "
                name="maritalStatus"
                rules={[{ required: true, message: 'Please select your marital status!' }]}
              >
                <Radio.Group onChange={onMaritalStatusChange} value={maritalStatus}>
                  <Radio style={{ marginRight: 20 }} value={'single'}>Độc thân</Radio>
                  <Radio value={'married'}>Đã kết hôn</Radio>
                </Radio.Group>
              </Form.Item>
            </Col>
          </Row>
          <Row gutter={[16, 0]}>
            {/* Thêm các trường nhập liệu khác ở đây */}
          </Row>
        </Form>
      </Row>
    </div>
  );
}
