import React from 'react'
import { Card } from 'antd';
const { Meta } = Card;

export default function NewsPage() {
  return (
    <div>
      
      <Card
        hoverable
        style={{ display: 'flex', alignItems: 'center', width: 'auto' }} // Điều chỉnh Card theo chiều ngang
      >
        <div style={{ marginRight: 20 }}> {/* Điều chỉnh khoảng cách giữa hình ảnh và văn bản */}
          <img alt="Ho Chi Minh" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" style={{ width: 240 }} />
        </div>
        <Meta
          title="Chủ tịch Hồ Chí Minh"
          description="Kế thừa và phát triển sáng tạo quan điểm của chủ nghĩa Mác - Lenin về việc dệ cao, coi trọng đạo đức của người cộng sản, Chủ tịch Hồ Chí Minh khẳng định phải xây dựng được đội ngũ cán bộ 'vừa hồng vừa chuyên'."
        />
      </Card>


    </div>
  )
}
