import React, { useState } from 'react';
import { Carousel, Radio } from 'antd';
import './Home.css';
const contentStyle = {
  height: '30px',
  lineHeight: '15px',
  textAlign: 'left',
  background: '#ccc',
};

export default function HomePage() {
  const [dotPosition, setDotPosition] = useState('top');

  const handlePositionChange = ({ target: { value } }) => {
    setDotPosition(value);
  };

  return (
    <div className="home">
      <Row>

            <Carousel autoplay>
          <div>
            <img src="https://www.baolongan.vn/image/news/2022/20220702/images/1-7-2022%20Hinh%201%20Can%20Giuoc%20doan%20tham%20dinh%20tinh%20tham%20dinh%202%20xa%20My%20Loc%20Phuoc%20Ly%20dat%20xa%20NTM%20nang%20cao.jpg" alt="" />
            <h3 style={contentStyle}>Thẩm định nông thôn mới nâng cao cho xã Vĩnh Công</h3>
          </div>
          <div>
          <img src="https://www.baolongan.vn/image/news/2020/20200526/images/IMG_1853.jpg" alt="" />

            <h3 style={contentStyle}>Đẩy mạnh công tác TDTT ở thanh thiếu niên</h3>
          </div>
          <div>
          <img src="https://www.baolongan.vn/image/news/2022/20220523/images/3.jpg" alt="" />

            <h3 style={contentStyle}>Xã Vĩnh Công hướng đến nông thôn mới nâng cao</h3>
          </div>
          <div>
          <img src="https://www.baolongan.vn/image/news/2021/20210423/images/81.gif" alt="" />

            <h3 style={contentStyle}>Đẩy mạnh công tác trấn áp tội phạm</h3>
          </div>
          
        </Carousel>
      </Row>
    </div>
  );
}
