import { Carousel, Radio } from 'antd';
import './Home.css';
const contentStyle = {
  height: '160px',
  // color: '#fff',
  lineHeight: '0px',
  textAlign: 'left',
  // background: '#364d79',
};

export default function HomePage() {
  const [dotPosition, setDotPosition] = useState('top');

  const handlePositionChange = ({ target: { value } }) => {
    setDotPosition(value);
  };

  return (
    <>
      
      <Carousel autoplay>
    <div>
      <img src="https://www.baolongan.vn/image/news/2022/20220702/images/1-7-2022%20Hinh%201%20Can%20Giuoc%20doan%20tham%20dinh%20tinh%20tham%20dinh%202%20xa%20My%20Loc%20Phuoc%20Ly%20dat%20xa%20NTM%20nang%20cao.jpg" alt="" />
      <h3 style={contentStyle}>1</h3>
    </div>
    <div>
    <img src="https://bcp.cdnchinhphu.vn/thumb_w/1900/334894974524682240/2024/5/6/anh-vieng-lang-1714963302603659010084-71-0-569-886-crop-17149705817191278670390.jpg" alt="" />

      <h3 style={contentStyle}>2</h3>
    </div>
    
  </Carousel>
    </>
  );
}
