import React from 'react';
import { useState, useEffect } from 'react';
import { Form, Input, Radio, DatePicker, Button, Row, Col, Select } from 'antd';
import './ID.css'

const { Option } = Select;


export default function Passport() {
  const [religions, setReligions] = useState([]);

  useEffect(() => {
    // Gọi hàm fetchData khi component được mount
    fetchData();
  }, []); // Tham số thứ hai là mảng dependencies rỗng, đảm bảo hàm chỉ chạy một lần

  const fetchData = async () => {
    try {
      // Lấy nội dung từ file religion.txt
      const response = await fetch('/assets/txt/religion.txt');
      const data = await response.text();
      
      // Chia nội dung thành các dòng
      const lines = data.split('\n');
      
      // Tạo mảng các Option từ các dòng
      const options = lines.map((line, index) => (
        <Option key={index} value={line.trim()}>
          {line.trim()}
        </Option>
      ));

      // Cập nhật state với mảng các Option
      setReligions(options);
    } catch (error) {
      console.error('Error reading file:', error);
    }
  };




  return (
    <div className='id'>
      <h1>Đăng ký hộ chiếu trực tuyến</h1>
      <Form
        name="passport_registration"
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 16 }}
        style={{ width: '80%', margin: '0 auto' }}
      >
        <Row >
          <Col span={12}>
            <Form.Item
              label="Họ và tên: "
              name="fullname"
              rules={[{ required: true, message: 'Please input your fullname!' }]}
            >
              <Input placeholder='Nhập họ và tên' />
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item
              label="Giới tính: "
              name="gender"
              rules={[{ required: true, message: 'Please select your gender!' }]}
            >
              <Radio.Group defaultValue={'male'} style={{ display: 'flex' }}>
                <Radio value={'male'} style={{ width: 80 }}>Nam</Radio>
                <Radio value={'female'} style={{ width: 80 }}>Nữ</Radio>
              </Radio.Group>
            </Form.Item>
          </Col>
        </Row>
        <Row >
          <Col span={12}>
            <Form.Item
              label="Ngày sinh: "
              name="dob"
              rules={[{ required: true, message: 'Please input your date of birth!' }]}
            >
              <Input type='date' />
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item
              label="Nơi sinh: "
              name="placeOfBirth"
              rules={[{ required: true, message: 'Please input your place of birth!' }]}
            >
              <Select placeholder='Chọn nơi đăng ký khai sinh'>
                <Option value="option1">Option 1</Option>
                <Option value="option2">Option 2</Option>
              </Select>
            </Form.Item>
          </Col>
        </Row>

        <Row gutter={16}>
          <Col span={12}>
            <Form.Item
              label="Số CCCD/CMND"
              name="idNumber"
              rules={[{ required: true, message: 'Please input your ID number!' }]}
            >
              <Input type="text" placeholder="Nhập số CCCD/CMND" />
            </Form.Item>
          </Col>
          <Col span={12}>
            <Form.Item
              label="Ngày cấp"
              name="issueDate"
              rules={[{ required: true, message: 'Please input issue date!' }]}
            >
              <DatePicker style={{ width: '100%' }} />
            </Form.Item>
          </Col>
        </Row>

        <Row gutter={16}>
          <Col span={12}>
          <Form.Item
              label="Dân tộc: "
              name="ethnicity"
              rules={[{ required: true, message: 'Please input your ethnicity!' }]}
            >
              <Select placeholder='Chọn dân tộc'>
                <Option value="option1">Option 1</Option>
                <Option value="option2">Option 2</Option>
              </Select>
            </Form.Item>
          </Col>
          <Col span={12}>
          <Form.Item
              label="Tôn giáo: "
              name="religion"
              rules={[{ required: true, message: 'Please input your religion!' }]}
            >
              <Select placeholder='Chọn tôn giáo'>
              {religions}
              </Select>
            </Form.Item>
          </Col>
        </Row>


        <Row gutter={16}>
          <Col span={12}>
          <Form.Item
          label="Nơi thường trú"
          name="permanentAddress"
          rules={[{ required: true, message: 'Please input your permanent address!' }]}
          >
          <Input placeholder="Nhập nơi thường trú" />
        </Form.Item>
          </Col>
          <Col span={12}>
          <Form.Item
          label="Số điện thoại"
          name="phoneNumber"
          rules={[{ required: true, message: 'Please input your phone number!' }]}
        >
          <Input type="number" placeholder="Nhập số điện thoại" />
        </Form.Item>
          </Col>
        </Row>
        
        <Row justify="center">
          <Form.Item wrapperCol={{ span: 16, offset: 8 }}>
            <Button  type="primary" htmlType="submit">Nộp đơn</Button>
          </Form.Item>
        </Row>
      </Form>
    </div>
  );
}
