import React, { useState } from 'react';
import { Button, Col, Form, Input, Row, Typography, Image } from 'antd';
import '../Login/Login.css';
// import backgroundImage from '../../assets/images/bg.svg';
// import Logo from '../../assets/images/logo.png';
// import firebase from '../../firebase/config';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate
// import Upload from '../../assets/images/upload.png';
const backgroundStyle = {
//   backgroundImage: `url(${backgroundImage})`,
  backgroundSize: 'cover',
  backgroundPosition: 'center',
};

const { Title } = Typography;

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 8 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 },
  },
};

const tailFormItemLayout = {
  wrapperCol: {
    xs: { span: 24, offset: 0 },
    sm: { span: 16, offset: 8 },
  },
};

export default function SignUp() {
  const [form] = Form.useForm();
  const navigate = useNavigate(); 

  const onFinish = (values) => {
    console.log('Received values of form: ', values);
  };
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');


  const [avatar, setAvatar] = useState(null);

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    setAvatar(file);
  };


  const submit = async (e) => {
    e.preventDefault();
    // try {
    //   const user = await firebase.auth().createUserWithEmailAndPassword(email, password);
    //   if (user) {
    //     alert('Account created successfully!');
    //     navigate('/login'); 
    //     }
    // } catch (error) {
    //   alert(error);
    // }
  };

  return (
    <div className='formClass' style={backgroundStyle}>
      <Row style={{
        boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
        borderRadius: '5px',
        border: '1px solid #fff',
        padding: '10px',
        backgroundColor: '#fff',
        paddingTop: '50px'
      }}>
        <Col style={{ marginBottom: 20 }}>
          <Row justify='center' >
            <Col>
              <Image width={150} src={Logo} />
            </Col>
            <Col style={{ marginLeft: 10 }}>
              <Title>Sign Up</Title>
            </Col>
          </Row>
          <Row>
            <Form
              {...formItemLayout}
              form={form}
              name="register"
              onFinish={onFinish}
              initialValues={{
                residence: ['zhejiang', 'hangzhou', 'xihu'],
                prefix: '86',
              }}
              style={{
                maxWidth: 600,
              }}
              scrollToFirstError
            >
              <Form.Item
                name="userName"
                label="User Name"
                rules={[
                  {
                    required: true,
                    message: 'Please input your user name!',
                    whitespace: true,
                  },
                ]}
              >
                <Input placeholder='User Name' onChange={(e) => setName(e.target.value)} />
              </Form.Item>
              <Form.Item
                name="email"
                label="E-mail"
                rules={[
                  {
                    type: 'email',
                    message: 'The input is not valid E-mail!',
                  },
                  {
                    required: true,
                    message: 'Please input your E-mail!',
                  },
                ]}
              >
                <Input placeholder='Email' onChange={(e) => setEmail(e.target.value)} />
              </Form.Item>
              <Form.Item
                name="password"
                label="Password"
                rules={[
                  {
                    required: true,
                    message: 'Please input your password!',
                  },
                ]}
                hasFeedback
              >
                <Input.Password placeholder='Password' />
              </Form.Item>
              <Form.Item

                name="confirm"
                label="Confirm Password"
                dependencies={['password']}
                hasFeedback
                rules={[
                  {
                    required: true,
                    message: 'Please confirm your password!',
                  },
                  ({ getFieldValue }) => ({
                    validator(_, value) {
                      if (!value || getFieldValue('password') === value) {
                        return Promise.resolve();
                      }
                      return Promise.reject(new Error('The new password that you entered do not match!'));
                    },
                  }),
                ]}
              >
                <Input.Password placeholder='Confirm Password' onChange={(e) => setPassword(e.target.value)} />
              </Form.Item>

             
                
                 <Form.Item>
                <input type="file" id='file' style={{ display: 'none' }} onChange={handleAvatarChange} />
                <label htmlFor="file" className='label-avt' style={{ cursor: 'pointer' }}>
                  <img src={Upload} alt="Add your avatar" width={35} style={{ marginLeft: '50px' }} />
                  <span className='span-avt'>{avatar ? avatar.name : 'Add an avatar'}</span>
                </label>
              </Form.Item>
                  
                

                
                
              
              
              <Row justify="end" style={{ marginBottom: '16px' }}>
                <Col>
                  <Form.Item noStyle>
                    Already have an Account <Link to="/login">Login</Link>?
                  </Form.Item>
                </Col>
              </Row>
              
              <Row>
                <Col offset={8}>
                  <Form.Item {...tailFormItemLayout} >
                    <Button type="primary" htmlType="submit" onClick={submit}>
                      Register
                    </Button>
                  </Form.Item>
                </Col>
                <Col>
                </Col>
              </Row>
            </Form>
          </Row>
        </Col>
      </Row>
    </div>
  );
}
