import React, { useState } from 'react';
import { Button, Form, Input, Row, Col, Image, Typography, Modal } from 'antd';
import './Login.css';
// import Logo from '../../assets/images/logo.png';
// import backgroundImage from '../../assets/images/bg.svg';
import { Link, useNavigate } from 'react-router-dom';
import firebase from '../../firebase/config';
import ForgotPassword from './ForgotPassword'; // Import Modal ForgotPassword

const { Title } = Typography;

// const backgroundStyle = {
//   backgroundImage: `url(${backgroundImage})`,
//   backgroundSize: 'cover',
//   backgroundPosition: 'center',
// };

const onFinish = (values) => {
  console.log('Success:', values);
};

const onFinishFailed = (errorInfo) => {
  console.log('Failed:', errorInfo);
};

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Sá»­ dá»¥ng hook useNavigate
  const [isModalOpen, setIsModalOpen] = useState(false); // State Ä‘á»ƒ kiá»ƒm soÃ¡t hiá»ƒn thá»‹ modal

  const showModal = () => {
    setIsModalOpen(true); // Má»Ÿ modal
  };

  const closeModal = () => {
    setIsModalOpen(false); // ÄÃ³ng modal
  };

  const submit = async (e) => {
    e.preventDefault();
    try {
      const user = await firebase.auth().signInWithEmailAndPassword(email, password);
      if (user) {
        alert('Login successfully!');
        navigate('/');
      }
    } catch (error) {
      alert(error);
    }
  };

  return (
    <div className='formClass' style={backgroundStyle}>
      <Row style={{
        boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
        borderRadius: '5px',
        border: '1px solid #fff',
        padding: '10px',
        backgroundColor: '#fff',
        paddingTop: '50px'
      }}>
        <Col style={{marginBottom:20}}>
          <Row justify='center' >
            <Col>
              <Image width={150} src={Logo}/>
            </Col>
            <Col style={{marginLeft:10}}>
              <Title>Login</Title>
            </Col>
          </Row>
          <Row justify='center'>
            <Form
              name="basic"
              labelCol={{ span: 8 }}
              wrapperCol={{ span: 16 }}
              style={{ maxWidth: 600 }}
              initialValues={{ remember: true }}
              onFinish={onFinish}
              onFinishFailed={onFinishFailed}
              autoComplete="off"
            >
              <Form.Item
                label="email"
                name="email"
                rules={[{ required: true, message: 'Please input your email!' }]}
              >
                <Input placeholder='Email' onChange={(e) => setEmail(e.target.value)} />
              </Form.Item>
              <Form.Item
                label="Password"
                name="password"
                rules={[{ required: true, message: 'Please input your password!' }]}
              >
                <Input.Password placeholder='Password' onChange={(e) => setPassword(e.target.value)} />
              </Form.Item>
              <Row justify="space-between" align="middle" style={{marginBottom:'20px'}}>
                <Col>
                  <Form.Item noStyle> 
                    Don't have an account? <Link to="/signup">Sign up</Link>
                  </Form.Item>
                </Col>
                <Col >
                  <Form.Item noStyle>
                    {/* ThÃªm sá»± kiá»‡n onClick Ä‘á»ƒ má»Ÿ modal */}
                    <a className="login-form-forgot" onClick={showModal}>Forgot password</a>
                  </Form.Item>
                </Col>
              </Row>
              <ForgotPassword visible={isModalOpen} closeModal={closeModal} />
              <Form.Item wrapperCol={{ offset: 10, span: 16 }}>
                <Button type="primary" htmlType="submit" onClick={submit}>Submit</Button>
              </Form.Item>
            </Form>

          </Row>
        </Col>
      </Row>
      {/* Modal ForgotPassword */}
      <ForgotPassword isModalOpen={isModalOpen} closeModal={closeModal} />
    </div>
  );
}
